import React, { Component } from 'react'
import firebase from 'firebase'
import firebaseConfig from '../../config'

class Login extends Component {
    constructor(props) {
      super(props);

      if (!firebase.apps.length) {
        firebase.initializeApp(firebaseConfig);
      } else {
        firebase.app(); // if already initialized, use that one
      }

      this.login = this.login.bind(this);
      this.handleChange = this.handleChange.bind(this);
      this.signup = this.signup.bind(this);
      this.state = {
        email: "",
        password: "",
        user: {}
      }
    }

    authListener() {
      firebase.auth().onAuthStateChanged((user) => {
        if (user) {
          this.setState({
            user
          })
          console.log("User adalah : " + user.email)
        }
        else {
          this.setState({
            user: null
          })
        }
      })
    }

    componentDidMount() {
      this.authListener();
    }

    login(e) {
      e.preventDefault();
      firebase.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u) => {
        console.log(u)
        //   (<Redirect to="/list-product" />)
      }).catch((err) => {
        console.log(err);
      })
    }

    signup(e) {
      e.preventDefault();
      firebase.auth().createUserWithEmailAndPassword(this.state.email, this.state.password).then((u) => {
        console.log(u)
      }).catch((err) => {
        console.log(err);
      })
    }

    onLoginGoogle = () => {
      var provider = new firebase.auth.GoogleAuthProvider();
      firebase.auth()
          .signInWithPopup(provider)
          .then((result) => {
              /** @type {firebase.auth.OAuthCredential} */
              // var credential = result.credential;

              // This gives you a Google Access Token. You can use it to access the Google API.
              // var token = credential.accessToken;
              // The signed-in user info.
              // var user = result.user;

          }).catch((error) => {
              console.log(error)
          });
    }

    handleChange(e) {
      this.setState({
        [e.target.name]: e.target.value
      })
    }

    logout() {
      firebase.auth().signOut();
      window.location.reload();
    }

    render() {
      return this.state.user ? (
        <div className="User">
          {/* <div></div> */}
          <img src={this.state.user.photoURL} alt="gambar" style={{width:"100px", height:"100px"}} />
          <center><h1>Welcome {this.state.user.email}</h1></center>
          <br /><br />
          <center><button onClick={this.logout}>Logout</button></center>
        </div>
      ) : (
        <div className="form">
          <div className="login">
          <center><p id="login-title">Masuk ke Samsung<br/> Account Anda</p></center>
          <form>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Email"
              onChange={this.handleChange}
              value={this.state.email}
            /> <br/>
            <input
              name="password"
              type="password"
              onChange={this.handleChange}
              id="password"
              placeholder="Password"
              value={this.state.password}
            /> <br/><br/>
            <center><button className="btn-login" onClick={this.login}>Login</button></center><br/>
            <center><button className="btn-login" onClick={this.signup}>Signup</button></center>
            <br/><br/>

            <center><p style={{marginTop: -30}}>atau</p></center>

            <center><button className="btn-login-g" onClick={this.onLoginGoogle()} > 
            <img src="https://www.freepnglogos.com/uploads/google-logo-png/google-logo-png-google-icon-logo-png-transparent-svg-vector-bie-supply-14.png"
              alt="logo" style={{width: 25, backgroundColor: "white", padding: 4}}/>
            Masuk dengan Google 
            </button></center>
          </form>
          </div>
        </div>
      )
    }
  }

export default Login;
