}

const About = () => {
  return (
    <div className="About">
      {/* <center><h2>Biodata</h2></center> */}
      <div id="bio">
        <p id="p-1">Hello,I'm</p>
        <p id="p-2">Fahmi Firmansyah</p>
        <p id="p-3">Teknik <p id="p-3-2">Informatika</p></p>
        <p id="p-4">kelas TI-3H</p>
