import React, { Component } from 'react'
import firebase from 'firebase';
import firebaseConfig from '../../config'
import Post from './Post'

export default class Forgot extends Component {
    constructor(props) {
        super(props)

        if (!firebase.apps.length) {
            firebase.initializeApp(firebaseConfig);
        } else {
            firebase.app(); // if already initialized, use that one
        }

        this.state = {
            nama: '',
            tempat: '',
            dataWisata: []
        }
    }

    componentDidMount(){
        this.getData();
    }

    handleOnChangeNama = (e) => {
        this.setState({
            nama: e.target.value,
        })
    }

    handleOnChangeTempat = (e) => {
        this.setState({
            tempat: e.target.value,
        })
    }

    tambahData = () => {
        firebase.database().ref('DTW').push({
            nama: this.state.nama,
            tempat: this.state.tempat
        })

        alert('Data Berhasil ditambahkan');
    }

    getData = () => {
        firebase.database().ref('DTW')
            .on('value', (snapshot) => {
                let newData = [];
                snapshot.forEach(data => {
                    const dataVal = data.val();
                    newData.push({
                        nama: dataVal.nama,
                        tempat: dataVal.tempat
                    })
                })
                this.setState({
                    dataWisata: newData
                })
            })
    }

    render() {
        return (
            <div>
                <label htmlFor="nama">Nama</label>
                <input type="text" onChange={this.handleOnChangeNama} /><br />
                <label htmlFor="tempat">Tempat</label>
                <input type="text" onChange={this.handleOnChangeTempat} /><br />
                <button onClick={this.tambahData}>Tambah</button>
                <br /><br /><br /><br />
                <p>List Data</p>
                {this.state.dataWisata.map(data => {
                    return (
                        <Post
                            nama={data.nama}
                            tempat={data.tempat}
                        />
                    )
                })}
            </div>
        )
    }
}
