import React from 'react'

const PostKeranjang = (brg) => {
  return (
    <tr>
      <td align="center">{brg.no}</td>
      {/* <td align="center">{brg.id}</td> */}
      <td align="center">{brg.nama}</td>
      <td align="center"><img src={brg.gambar} alt="gambar" width="150" height="150" /></td>
      <td align="center">{brg.harga}</td>
      <td align="center"></td>
      <td align="center">{brg.harga * brg.qty}</td>
      <td align="center">
        <button style={{marginRight:"10px"}} className="btn-hapus-keranjang" onClick={() => { if (window.confirm("Apakah anda yakin mengupdate artikel ini ?")) brg.updateTelevisi(brg.id, brg.nama, brg.harga, brg.gambar, brg.stok, brg.qty) }}>Update</button>
        <button className="btn-hapus-keranjang" onClick={() => { if (window.confirm("Apakah anda yakin menghapus artikel ini ?")) brg.hapusTelevisi(brg.id) }}>Hapus</button>
      </td>
    </tr>
  )
}

export default PostKeranjang;
