class Keranjang extends Component {

  constructor(props) {
    super(props);

    if (!firebase.apps.length) {
      firebase.initializeApp(firebaseConfig);
    } else {
      firebase.app(); // if already initialized, use that one
    }

    this.state = {
      listKeranjang: [],
      user: {}
    }

    this.authListener();

  }

  authListener() {
    firebase.auth().onAuthStateChanged((user) => {
      if (user) {
        this.setState({
          user
        })

        this.ambilDataDariServerAPI(user.uid);

        console.log("User adalah : " + user.email)
      }
      else {
        this.setState({
          user: null
        })
      }
    })
  }

  ambilDataDariServerAPI = (uid) => {
    // this.authListener();
    // console.log("uid: "+this.state.user.uid)
    const userRef = firebase.database().ref('keranjang/'+this.state.user.uid);
    userRef.once('value', (snapshot) => {
      let newUserState = [];
      snapshot.forEach(data => {
        const dataVal = data.val()
        newUserState.push({
          id: data.key,
          nama: dataVal.nama,
          harga: dataVal.harga,
          gambar: dataVal.gambar,
          stok: dataVal.stok,
          qty: dataVal.qty
        })
      })
      this.setState({
        listKeranjang: newUserState
      })
      // console.log(this.state);
    })
  }

  // componentWillMount() {
  //   this.authListener();
  // }

  componentDidMount() {
    // this.authListener();
    this.ambilDataDariServerAPI();
  }

  componentDidUpdate(prevProps, prevState) {
    // this.ambilDataDariServerAPI();
  }

  render() {
    var no = 0;
    var total = 0;
    return (
      <div className="post-televisi">
        {
                /* <div className="form pb-2 border-bottom">
                    <button type="submit" className="btn btn-primary" onClick={this.handleTombolSimpan}>Simpan</button>
                </div> */}
        <center><h2>Keranjang</h2></center>
        <div className="tgh">
          <table border="1" cellPadding="5" width="100%">
            <tr>
              <th align="center">No</th>
              {/* <th align="center">ID Produk</th> */}
              <th align="left">Nama</th>
              <th align="center">Gambar</th>
              <th align="center">Harga</th>
              <th align="center">Qty</th>
              <th align="center">Subtotal</th>
            </tr>
            {
              this.state.listKeranjang.map(televisi => {
                no += 1;
                total += televisi.harga * televisi.qty
                return (
                  <PostKeranjang
                    no={no}
                    key={televisi.id}
                    id={televisi.id}
                    nama={televisi.nama}
                    harga={televisi.harga}
                    gambar={televisi.gambar}
                    stok={televisi.stok}
                    qty={televisi.qty}
                    tambahTelevisi={this.handleGetTelevisi} />
                )
              })
            }
            <tr>
              <td colSpan="5" align="right">Total : </td>
              <td align="center">{total}</td>
            </tr>
          </table>
        </div>
      </div>
    )
  }
}

