import React, { Component } from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  // Redirect
} from "react-router-dom";
import foto from './FAHMI.jpg'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import './Televisi.css';
// import firebase from 'firebase';
// import firebaseConfig from './config';
import 'font-awesome/css/font-awesome.min.css';
import { connect } from 'react-redux'
import Login from './component/login/Login'
import Televisi from './component/televisi/Televisi'
import Keranjang from './component/keranjang/Keranjang'
import firebase from 'firebase'
import firebaseConfig from './config'

// var coba = 0;

class App extends Component {
  constructor(props) {
    super(props);

    if (!firebase.apps.length) {
        firebase.initializeApp(firebaseConfig);
    } else {
        firebase.app(); // if already initialized, use that one
    }

    this.state = {
        listKeranjang: [],
        user: {}
    }
  }

  authListener() {
    firebase.auth().onAuthStateChanged((user) => {
        if (user) {
            this.setState({
                user
            })

            this.ambilDataDariServerAPI(user.uid);

            console.log("User adalah : " + user.email)
        }
        else {
            this.setState({
                user: null
            })
        }
    })
}

  ambilDataDariServerAPI = (uid) => {
    // this.authListener();
    // console.log("uid: "+this.state.user.uid)
    const userRef = firebase.database().ref('keranjang/' + this.state.user.uid);
    userRef.once('value', (snapshot) => {
        let newUserState = [];
        let coba = 0;
        snapshot.forEach(data => {
            const dataVal = data.val()
            newUserState.push({
                id: data.key,
                nama: dataVal.nama,
                harga: dataVal.harga,
                gambar: dataVal.gambar,
                stok: dataVal.stok,
                qty: dataVal.qty
            })
            coba += dataVal.qty
            console.log('Coba : '+ coba)
            this.props.handleKeranjang(coba)
        })
        this.setState({
            listKeranjang: newUserState
        })
        console.log(this.state);
    })
  }

  componentDidMount() {
    this.authListener();
    this.ambilDataDariServerAPI();
  }

  render() {
    console.log(this.props)
    return (
      <Router>
        <div>

          <ul className="menu">
            <img src="https://media.wired.com/photos/5ed7d5b17f41315c30f0d5d1/master/w_2611,h_512,c_limit/5017.png" alt="Gambar" />
            <li>
              <Link to="/login" ><i className="fa fa-user-circle"></i></Link>
            </li>
            <li>
              <Link to="/keranjang" onClick={this.props.handleKeranjang}><i className="fa fa-shopping-cart"></i><span id="cart">{this.props.tOrder}</span></Link>
            </li>
            <li>
              <Link to="/about" ><span>About</span></Link>
            </li>
            {/* <li>
              <Link to="/keranjang" ><span>Keranjang</span></Link>
            </li> */}
            <li>
              <Link to="/list-product" ><span>List Produk</span></Link>
            </li>
            <li>
              <Link to="/" ><span>Home</span></Link>
            </li>
          </ul>

          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            <Route path="/list-product">
              <Products />
            </Route>
            <Route path="/keranjang">
              <Keranjangs />
            </Route>
            <Route path="/about">
              <About />
            </Route>
            <Route path="/login">
              <Login />
            </Route>
          </Switch>
        </div>
      </Router>
    );
  }
}


